mapper:
    resultType指定返回值类型
    
        select name,date,start_time,end_time,valid from card,student where card.stu_id=student.id order by start_time desc limit #{startData},#{endData};
    

    #{}不能用于模糊查询
    
        select name,date,start_time,end_time,valid from card,student where card.stu_id=student.id and name like '%${name}%' limit #{startData},#{endData};
    

    
        select name,date,start_time,end_time,valid from card,student where card.stu_id=student.id order by start_time asc limit #{startData},#{endData};
